using BusinessObjects.Models;
using System.Text.Encodings.Web;
using WebNewsAPIs.Dtos;

namespace WebNewsAPIs.Services
{
    public static class HtmlHelper
    {
        public static string GetHtmlForSendMailRegister(User userDto, string code)
        {
            string html = $"<!DOCTYPE html>\r\n<html lang=\"en\">\r\n  <head>\r\n    <meta charset=\"UTF-8\" />\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\" />\r\n    <title>Thong bao thanh cong</title>\r\n    <link\r\n      rel=\"stylesheet\"\r\n      href=\"https://cdn.jsdelivr.net/npm/bootstrap@4.6.2/dist/css/bootstrap.min.css\"\r\n    />\r\n    <script src=\"https://cdn.jsdelivr.net/npm/jquery@3.7.1/dist/jquery.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/popper.js@1.16.1/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/bootstrap@4.6.2/dist/js/bootstrap.bundle.min.js\"></script>\r\n  </head>\r\n  <body>\r\n    <div class=\"card text-white bg-success mb-3\" style=\"max-width: 20rem\">\r\n      <div class=\"card-header\">Thông báo tạo tài khoản thành Công</div>\r\n      <div class=\"card-body\">\r\n        <h4 class=\"card-title\">{userDto.DisplayName}</h4>\r\n        <p class=\"card-text\">\r\n          Từ bay giờ bạn có thẻ dùng tài khoản này để tham gia đọc báo và tương\r\n          tác.\r\n        </p>\r\n        <button class=\"btn btn-primary\">\r\n          <a\r\n            href=\"https://localhost:7069/User/Login\"\r\n            target=\"_blank\"\r\n            class=\"text-decoration-none text-white\"\r\n            >Bấm vào đây để login</a\r\n          >\r\n <button class=\"btn btn-primary\">\r\n          <a\r\n            href=\"https://localhost:7069/User/NotificationConfirmAccount?userId={userDto.UserId}&code={HtmlEncoder.Default.Encode(code)}\"\r\n            target=\"_blank\"\r\n            class=\"text-decoration-none text-white\"\r\n            >Bấm vào đây ConfirmEmail</a\r\n          >\r\n        </button>\r\n      </div>\r\n    </div>\r\n\r\n    <script\r\n      src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery/3.7.1/jquery.min.js\"\r\n      integrity=\"sha512-v2CJ7UaYy4JwqLDIrZUI/4hqeoQieOmAZNXBeQyjo21dadnwR+8ZaIJVT8EE2iyI61OV8e6M8PP2/4hpQINQ/g==\"\r\n      crossorigin=\"anonymous\"\r\n      referrerpolicy=\"no-referrer\"\r\n    ></script>\r\n    <link\r\n      rel=\"stylesheet\"\r\n      href=\"//cdnjs.cloudflare.com/ajax/libs/toastr.js/latest/toastr.min.css\"\r\n    />\r\n    <script src=\"//cdnjs.cloudflare.com/ajax/libs/toastr.js/latest/toastr.min.js\"></script>\r\n    <script type=\"text/javascript\">\r\n      toastr.options = {{\r\n        closeButton: false,\r\n        debug: false,\r\n        newestOnTop: true,\r\n        progressBar: true,\r\n        positionClass: \"toast-top-right\",\r\n        preventDuplicates: true,\r\n        onclick: null,\r\n        showDuration: \"300\",\r\n        hideDuration: \"1000\",\r\n        timeOut: \"5000\",\r\n        extendedTimeOut: \"1000\",\r\n        showEasing: \"swing\",\r\n        hideEasing: \"linear\",\r\n        showMethod: \"fadeIn\",\r\n        hideMethod: \"fadeOut\",\r\n      }};\r\n      setInterval(function () {{\r\n        toastr[\"success\"](\"Bạn đã tạo tài khoản thành Công\");\r\n      }}, 300);\r\n    </script>\r\n  </body>\r\n</html>\r\n";
            return html;
        }
    }
}
